c = mpz()

phi_N1 = mpz(p-1)*mpz(q-1)
e = mpz(65537)
d = invert(e,phi_N1)

m = powmod(c,d,N1)
hex_m = ''

while m > 0:
	mod = f_mod(m,16)
	hex_m = str(hex(mod))[2:]+hex_m
	m = (m-mod)/16

hex_m = '0x0'+hex_m
plain_m = '466163746f72696e67206c65747320757320627265616b205253412e'

def hex_2_string(str_i):
        str_o = ""
        i=0
        while i < len(str_i):
                str_o = str_o + chr(int(str_i[i:i+2],16))
                i+=2
        return str_o

